import type { NextPage } from "next";
import Head from "next/head";
import Image from "next/image";
import BlogCard from "../components/BlogCard";
import BlurImage from "../components/BlurImage";
import FeaturedPost from "../components/FeaturedPost";
import Header from "../components/Header";
import { Post } from "../types";
import { api, Response } from "../utils/api";

type Props = {
  posts: [Post];
};

const Home: NextPage<Props> = (props) => {
  const { posts } = props;
  return (
    <div>
      <Head>
        <title>Create Next App</title>
        <meta name="description" content="Generated by create next app" />
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <Header />
      <main className="mt-40">
        {posts.length > 0 ? (
          <FeaturedPost data={posts[0]} />
        ) : (
          <div className="flex flex-col justify-center items-center py-20">
            <BlurImage
              src="/empty-state.png"
              alt="No Posts"
              width={613}
              height={420}
              placeholder="blur"
              blurDataURL="data:image/png;base64,"
            />
            <p className="text-2xl font-cal text-gray-600">No posts yet.</p>
          </div>
        )}
        {posts.length > 1 && (
          <div className="mx-5 lg:mx-24 2xl:mx-auto mb-20 max-w-screen-xl">
            <h2 className="font-cal text-4xl md:text-5xl mb-10">
              More stories
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-x-4 gap-y-8 w-full">
              {posts.slice(1).map((data, index) => (
                <BlogCard key={index} data={data} />
              ))}
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export async function getStaticProps() {
  // Get the posts from the component service
  const data = await api<Response>(
    "https://render.develop.components.dev.dx.squiz.cloud/r/sync/sync/search-news-listing/0.0.4/json?query=sustainability"
  ).catch((error) => {
    /* show error message */
    console.log("error");
    console.log(error);
  });

  console.log(data);
  const posts = data || [];

  return {
    props: {
      posts,
    }, // will be passed to the page component as props
  };
}

export default Home;
